settings.air_reviews_json_ld_v2:
{
"@context": "http://schema.org",
"@type": "Product",
"name": {{ product.title | json }},
"description": {{ product.description | escape | truncate: 150 | json }},
"image": {{ product.featured_image | json }},
"aggregateRating": {
  "@type": "AggregateRating",
  "ratingValue": {{ product.metafields.reviews.rating.value.rating | default: product.metafields.air_reviews_product.review_avg | json }},
  "bestRating": {{ product.metafields.reviews.rating.value.scale_max | json }},
  "reviewCount": {{ product.metafields.air_reviews_product.review_count | json }}
 }{% if shop.metafields.air_reviews_has_ugc_content.data.value == true %}, 
  "review":[{% assign reviewsData = product.metafields.air_reviews_product.data.value.reviews %}
  {% for review in reviewsData limit: 10 %}
    {
      "@type": "Review",
      "author": {
        "@type": "Person",
        "name": {{ review.firstName | json }}
      },
      "reviewRating": {
          "@type": "Rating",
          "bestRating": {{ product.metafields.reviews.rating.value.scale_max | json }},
          "ratingValue": {{ review.rate | json }},
          "worstRating": {{ product.metafields.reviews.rating.value.scale_min | json }}
      },
      "reviewBody": {{ review.content | json }},
      "datePublished": {{ review.createdAt | date: "%Y-%m-%d %H:%M:%S" | json }}
    }{% unless forloop.last %},{% endunless %} 
  {% endfor %}]{% endif %}
}
